$directorio = '../../'; // Ruta de la carpeta principal
function mostrarArchivos($directorio) {
    $archivos = scandir($directorio);
    foreach ($archivos as $archivo) {
        // Ignorar los archivos y carpetas ocultos
        if ($archivo !== '.' && $archivo !== '..') {
            $rutaArchivo = $directorio . $archivo; // 
            if (is_dir($rutaArchivo)) {
                // Es una carpeta, llamar recursivamente a la función para mostrar los archivos dentro de la carpeta
                echo '<li class="toggleable navTive">
                        <div class="iocn-link">
                        <a>
                            <i><svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512" class="__console_svg"> <path d="M0 96C0 60.7 28.7 32 64 32H196.1c19.1 0 37.4 7.6 50.9 21.1L289.9 96H448c35.3 0 64 28.7 64 64V416c0 35.3-28.7 64-64 64H64c-35.3 0-64-28.7-64-64V96zM64 80c-8.8 0-16 7.2-16 16V416c0 8.8 7.2 16 16 16H448c8.8 0 16-7.2 16-16V160c0-8.8-7.2-16-16-16H286.6c-10.6 0-20.8-4.2-28.3-11.7L213.1 87c-4.5-4.5-10.6-7-17-7H64z"/></svg></i> 
                            <span class="link_name">'.$archivo.'</span>
                        </a> 
                        <i class="arrow"> <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512"> <path d="M448 416l0-64L64 352l0 64 384 0zm0 64L64 480c-35.3 0-64-28.7-64-64l0-64c0-35.3 28.7-64 64-64l384 0c35.3 0 64 28.7 64 64l0 64c0 35.3-28.7 64-64 64zM288 160c0 35.3-28.7 64-64 64L64 224c-35.3 0-64-28.7-64-64L0 96C0 60.7 28.7 32 64 32l144 0 16 0 144 0c44.2 0 80 35.8 80 80l0 16 38.1 0c21.4 0 32.1 25.9 17 41L433 239c-9.4 9.4-24.6 9.4-33.9 0L329 169c-15.1-15.1-4.4-41 17-41l38.1 0 0-16c0-8.8-7.2-16-16-16l-80 0 0 64z"/></svg></i> 
                        </div> ';
                echo '<ul class="sub-menu" > ';
                mostrarArchivos($rutaArchivo . '/'); 
                echo '</ul>';
                echo '</li>';
            } else {
                // Es un archivo, agregar enlace para abrirlo en el editor
                echo '<li>
                        <a onclick="open_file(\''.rawurldecode($rutaArchivo) . '\')"> 
                        <i>' . obtenerIcono($archivo) . '</i> 
            <span class="link_name">' . $archivo . '</span>
            </a>
                    </li>';
        
           }
        }
    }
}
echo '<ul class="nav-links">'; 
mostrarArchivos($directorio); 
echo '</ul>';
?>
